@keyframes glitch {
  0% {
    transform: translate(0, 0) scale(1) skew(0);
    filter: hue-rotate(0deg) saturate(100%) contrast(100%) brightness(100%);
  }
  5% {
    transform: translate(-5px, -2px) scale(1.1) skew(9deg);
    filter: hue-rotate(360deg) saturate(300%) contrast(180%) brightness(150%) sepia(60%);
  }
  10% {
    transform: translate(4px, 2px) scale(0.9) skew(-12deg);
    filter: hue-rotate(180deg) saturate(480%) contrast(240%) brightness(130%) invert(60%);
  }
  15% {
    transform: translate(-6px, 1px) scale(1.15) skew(6deg);
    filter: hue-rotate(90deg) saturate(360%) contrast(300%) brightness(180%) blur(0.6px);
  }
  20% {
    transform: translate(7px, -3px) scale(0.85) skew(-15deg);
    filter: hue-rotate(270deg) saturate(600%) contrast(360%) brightness(110%) sepia(30%);
  }
  25% {
    transform: translate(-4px, 5px) scale(1.2) skew(18deg);
    filter: hue-rotate(45deg) saturate(420%) contrast(210%) brightness(200%) invert(30%);
  }
  30% {
    transform: translate(9px, -1px) scale(0.95) skew(-9deg);
    filter: hue-rotate(315deg) saturate(240%) contrast(160%) brightness(115%) blur(0.3px);
  }
  35% {
    transform: translate(-2px, -5px) scale(1.12) skew(24deg);
    filter: hue-rotate(135deg) saturate(540%) contrast(270%) brightness(140%) sepia(48%);
  }
  40% {
    transform: translate(5px, 4px) scale(0.97) skew(-21deg);
    filter: hue-rotate(225deg) saturate(180%) contrast(150%) brightness(135%) invert(15%);
  }
  45% {
    transform: translate(-7px, -1px) scale(1.21) skew(12deg);
    filter: hue-rotate(60deg) saturate(720%) contrast(330%) brightness(170%) blur(1.2px);
  }
  50% {
    transform: translate(6px, -4px) scale(0.88) skew(-27deg);
    filter: hue-rotate(300deg) saturate(300%) contrast(240%) brightness(105%) sepia(60%);
  }
  55% {
    transform: translate(-8px, 2px) scale(1.06) skew(15deg);
    filter: hue-rotate(150deg) saturate(480%) contrast(180%) brightness(195%) invert(45%);
  }
  60% {
    transform: translate(4px, -5px) scale(1.24) skew(-6deg);
    filter: hue-rotate(210deg) saturate(360%) contrast(300%) brightness(125%) blur(0.9px);
  }
  65% {
    transform: translate(-5px, 2px) scale(0.82) skew(30deg);
    filter: hue-rotate(30deg) saturate(660%) contrast(270%) brightness(155%) sepia(36%);
  }
  70% {
    transform: translate(8px, -4px) scale(1.15) skew(-18deg);
    filter: hue-rotate(330deg) saturate(240%) contrast(210%) brightness(175%) invert(54%);
  }
  75% {
    transform: translate(-3px, 5px) scale(0.91) skew(21deg);
    filter: hue-rotate(120deg) saturate(420%) contrast(150%) brightness(120%) blur(0.5px);
  }
  80% {
    transform: translate(7px, -2px) scale(1.09) skew(-24deg);
    filter: hue-rotate(240deg) saturate(540%) contrast(360%) brightness(160%) sepia(24%);
  }
  85% {
    transform: translate(-4px, -5px) scale(0.94) skew(27deg);
    filter: hue-rotate(75deg) saturate(300%) contrast(240%) brightness(145%) invert(18%);
  }
  90% {
    transform: translate(2px, 3px) scale(1.18) skew(-12deg);
    filter: hue-rotate(285deg) saturate(480%) contrast(180%) brightness(185%) blur(0.6px);
  }
  95% {
    transform: translate(-7px, -2px) scale(0.97) skew(9deg);
    filter: hue-rotate(165deg) saturate(360%) contrast(300%) brightness(125%) sepia(42%);
  }
  100% {
    transform: translate(0, 0) scale(1) skew(0);
    filter: hue-rotate(0deg) saturate(100%) contrast(100%) brightness(100%);
  }
}

.animate-glitch {
  animation: glitch 0.4s cubic-bezier(0.68, -0.55, 0.265, 1.55);
}

.fade-in-up {
  animation: fadeInUp 0.8s ease-out forwards;
  opacity: 0;
  transform: translateY(20px);
}

@keyframes fadeInUp {
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

/* Smooth scrolling container */
.smooth-scroll-container {
  transform: translate3d(0, 0, 0);
  backface-visibility: hidden;
  perspective: 1000px;
  will-change: transform;
}

.smooth-scroll-container * {
  transform: translate3d(0, 0, 0);
  backface-visibility: hidden;
}

/* Right to left scrolling animation */
@keyframes scrollRightToLeft {
  from {
    transform: translateX(0);
  }
  to {
    transform: translateX(-100%);
  }
}

/* Left to right scrolling animation */
@keyframes scrollLeftToRight {
  from {
    transform: translateX(-100%);
  }
  to {
    transform: translateX(0);
  }
}

/* Agora page animations */
.animate-fadeIn {
  animation: fadeIn 0.8s ease-out forwards;
  opacity: 0;
}

@keyframes fadeIn {
  from {
    opacity: 0;
    transform: translateY(10px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
}

