import { useState, useRef, useEffect } from "react";
import { Link, useLocation } from "wouter";
import { Menu, X } from "lucide-react";
import { SiX, SiGithub } from "react-icons/si";
import agoraIcon from "@assets/Untitled design_1754878809797.gif";
import docsIcon from "@assets/Untitled design_1754879488364.gif";
import contributeIcon from "@assets/Untitled design (1)_1754880001361.gif";
import venusIcon from "@assets/Untitled design (2)_1754882967112.gif";
import venusLogo from "@assets/Untitled design (2)_1754930655351.png";

export default function Navigation() {
  const [location] = useLocation();
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [docsDropdownOpen, setDocsDropdownOpen] = useState(false);
  const [agoraDropdownOpen, setAgoraDropdownOpen] = useState(false);
  const [contributeDropdownOpen, setContributeDropdownOpen] = useState(false);
  const [venusDropdownOpen, setVenusDropdownOpen] = useState(false);
  const [scrollY, setScrollY] = useState(0);
  const [glitchActive, setGlitchActive] = useState(false);
  const dropdownRef = useRef<HTMLDivElement>(null);
  const agoraDropdownRef = useRef<HTMLDivElement>(null);
  const contributeDropdownRef = useRef<HTMLDivElement>(null);
  const venusDropdownRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    function handleClickOutside(event: MouseEvent) {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target as Node)) {
        setDocsDropdownOpen(false);
      }
      if (agoraDropdownRef.current && !agoraDropdownRef.current.contains(event.target as Node)) {
        setAgoraDropdownOpen(false);
      }
      if (contributeDropdownRef.current && !contributeDropdownRef.current.contains(event.target as Node)) {
        setContributeDropdownOpen(false);
      }
      if (venusDropdownRef.current && !venusDropdownRef.current.contains(event.target as Node)) {
        setVenusDropdownOpen(false);
      }
    }

    function handleScroll() {
      // Don't track scroll on Agora page since it prevents scrolling
      if (location !== "/agora") {
        setScrollY(window.scrollY);
      }
    }

    document.addEventListener("mousedown", handleClickOutside);
    window.addEventListener("scroll", handleScroll);

    // Set initial scroll position - force visibility on Agora
    if (location === "/agora") {
      setScrollY(100); // Force navigation visibility on Agora
    } else {
      setScrollY(window.scrollY);
    }

    // Glitch effect disabled

    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  // Reset scroll awareness when location changes
  useEffect(() => {
    if (location === "/agora") {
      setScrollY(100); // Force visibility on Agora (prevents scrolling conflicts)
    } else if (location !== "/") {
      setScrollY(100); // Force visibility on non-home pages
    } else {
      setScrollY(window.scrollY); // Use actual scroll position on home page
    }
  }, [location]);

  const isActive = (path: string) => {
    if (path === "/" && location === "/") return true;
    if (path !== "/" && location.startsWith(path)) return true;
    return false;
  };

  // Calculate navigation opacity - Simple logic: only fade on home page, always visible everywhere else
  const navOpacity = location === "/" ? Math.min(1, scrollY / 100) : 1;

  const venusLinks = [
    { href: "/", label: "THE VENUS PROJECT" },
    { href: "/nfts", label: "NFTs" },
  ];

  const contributeLinks = [
    { href: "/contribute", label: "Contribute" },
  ];

  const agoraLinks = [
    { href: "/agora", label: "Agora" },
  ];

  const docsLinks = [
    { href: "/manifesto", label: "Manifesto" },
    { href: "/structure", label: "Structure" },
    { href: "/contact", label: "Contact Us" },
  ];

  return (
    <nav 
      className="fixed top-0 left-0 right-0 z-[9999] h-16" 
      style={{ 
        backgroundColor: location === "/" ? 'transparent' : 'rgba(255, 255, 255, 0.95)', 
        backdropFilter: location === "/" ? 'none' : 'blur(8px)',
        position: 'fixed',
        zIndex: 9999,
        display: 'block',
        visibility: 'visible'
      }}
    >

      <div className="w-full">
        <div className="flex items-center justify-between h-16 pr-4">
          {/* Left Logo - Home Link - FORCED visibility on Agora and non-home pages */}
          <div 
            className="flex items-center transition-opacity duration-500 ease-in-out pl-4" 
            style={{ opacity: navOpacity }}
          >
            <Link href="/">
              <img 
                src={venusLogo} 
                alt="Venus Project Home" 
                className="h-10 w-10 hover:opacity-75 transition-opacity duration-300 cursor-pointer"
              />
            </Link>
          </div>

          {/* Right Navigation Group - Desktop - FORCED visibility on Agora and non-home pages */}
          <div className="hidden md:block transition-opacity duration-500 ease-in-out" style={{ opacity: navOpacity }}>
            <div className="flex items-baseline space-x-2">
              <div 
                className="relative group" 
                ref={venusDropdownRef}
                onMouseEnter={() => setVenusDropdownOpen(true)}
                onMouseLeave={() => setVenusDropdownOpen(false)}
              >
                <button className="px-2 py-2 text-black focus:outline-none">
                  <img 
                    src={venusIcon} 
                    alt="Venus Project" 
                    className="h-8 w-8"
                  />
                </button>
                {venusDropdownOpen && (
                  <div 
                    className="absolute right-0 top-full w-48 bg-white border border-venus-gray rounded-md shadow-lg z-50"
                    onMouseEnter={() => setVenusDropdownOpen(true)}
                    onMouseLeave={() => setVenusDropdownOpen(false)}
                  >
                    {venusLinks.map((link) => (
                      <Link key={link.href} href={link.href}>
                        <span
                          className="block px-4 py-2 text-sm text-black hover:bg-gray-50 hover:text-venus-lime transition-all duration-300 ease-out transform hover:translate-x-1 cursor-pointer"
                        >
                          {link.label}
                        </span>
                      </Link>
                    ))}
                  </div>
                )}
              </div>

              <div 
                className="relative group" 
                ref={agoraDropdownRef}
                onMouseEnter={() => setAgoraDropdownOpen(true)}
                onMouseLeave={() => setAgoraDropdownOpen(false)}
              >
                <button className="px-2 py-2 text-black focus:outline-none">
                  <img 
                    src={agoraIcon} 
                    alt="Agora" 
                    className="h-8 w-8"
                  />
                </button>
                {agoraDropdownOpen && (
                  <div 
                    className="absolute right-0 top-full w-32 bg-white border border-venus-gray rounded-md shadow-lg z-50"
                    onMouseEnter={() => setAgoraDropdownOpen(true)}
                    onMouseLeave={() => setAgoraDropdownOpen(false)}
                  >
                    {agoraLinks.map((link) => (
                      <Link key={link.href} href={link.href}>
