    setIsConnected(true);
    setWalletAddress(address);
  };

  const handleGPUToggle = () => {
    setIsGPURunning(!isGPURunning);
  };

  const gpuStats = {
    flopsContributed: 2847392847,
    decisionCyclePowered: 12.7,
    hoursRunning: 3.2
  };

  return (
    <div className="min-h-screen bg-white text-black">
