import { WebSocketServer, WebSocket } from 'ws';
import { Server } from 'http';

interface StreamingMessage {
  id: string;
  from: string;
  to: string;
  content: string;
  isComplete: boolean;
  timestamp: number;
  confidence?: number;
  topic?: string;
}

interface AgoraClient {
  ws: WebSocket;
  id: string;
  lastActivity: number;
}

export class AgoraWebSocketServer {
  private wss: WebSocketServer;
  private clients: Map<string, AgoraClient> = new Map();
  private messageStreams: Map<string, NodeJS.Timeout> = new Map();

  constructor(server: Server) {
    this.wss = new WebSocketServer({ 
      server, 
      path: '/ws/agora',
      perMessageDeflate: {
        // Enable per-message deflate for better performance
        threshold: 1024,
        concurrencyLimit: 10,
        serverMaxWindowBits: 13,
        serverNoContextTakeover: false,
        clientMaxWindowBits: 13,
        clientNoContextTakeover: false
      }
    });

    this.wss.on('connection', this.handleConnection.bind(this));
    console.log('AGORA WebSocket server initialized on /ws/agora');
  }

  private handleConnection(ws: WebSocket) {
    const clientId = this.generateClientId();
    const client: AgoraClient = {
      ws,
      id: clientId,
      lastActivity: Date.now()
    };

    this.clients.set(clientId, client);
    console.log(`AGORA client connected: ${clientId}`);

    // Send welcome message
    this.sendToClient(clientId, {
      id: 'welcome',
      from: 'system',
      to: clientId,
      content: 'Connected to AGORA neural network',
      isComplete: true,
      timestamp: Date.now(),
      confidence: 1
    });

    ws.on('message', (data) => {
      try {
        const message = JSON.parse(data.toString());
        this.handleClientMessage(clientId, message);
      } catch (error) {
        console.error('Invalid WebSocket message:', error);
      }
    });

    ws.on('close', () => {
      this.clients.delete(clientId);
      this.cleanupClientStreams(clientId);
      console.log(`AGORA client disconnected: ${clientId}`);
    });

    ws.on('error', (error) => {
      console.error(`WebSocket error for client ${clientId}:`, error);
    });

    // Start streaming simulated neural activity
    this.initializeNeuralActivity(clientId);
  }

  private handleClientMessage(clientId: string, message: any) {
    const client = this.clients.get(clientId);
    if (!client) return;

    client.lastActivity = Date.now();

    // Broadcast to other clients (for multi-user scenarios)
    this.broadcastMessage(message, clientId);
  }

  private broadcastMessage(message: StreamingMessage, excludeClientId?: string) {
    this.clients.forEach((client, id) => {
      if (id !== excludeClientId && client.ws.readyState === WebSocket.OPEN) {
        this.sendToClient(id, message);
      }
    });
  }

  private sendToClient(clientId: string, message: StreamingMessage) {
    const client = this.clients.get(clientId);
    if (client && client.ws.readyState === WebSocket.OPEN) {
      try {
        client.ws.send(JSON.stringify(message));
      } catch (error) {
        console.error(`Failed to send message to client ${clientId}:`, error);
      }
    }
  }

  private initializeNeuralActivity(clientId: string) {
    const agents = ['alpha', 'beta', 'gamma', 'delta', 'epsilon', 'zeta', 'eta', 'theta', 'iota', 'kappa'];
    const topics = [
      'infrastructure optimization',
      'energy distribution',
      'resource allocation',
      'social coordination',
      'ecological balance',
      'transportation flow',
      'health systems',
      'education protocols',
      'governance structures',
      'innovation networks'
    ];

    const createStreamingMessage = () => {
      const fromAgent = agents[Math.floor(Math.random() * agents.length)];
      const toAgent = agents.filter(a => a !== fromAgent)[Math.floor(Math.random() * (agents.length - 1))];
      const topic = topics[Math.floor(Math.random() * topics.length)];

      const messageTemplates = [
        `Analyzing ${topic} patterns - detecting optimization opportunities`,
        `Coordinating with ${toAgent} systems for ${topic} enhancement`,
        `Processing ${topic} data streams - confidence levels rising`,
        `Implementing ${topic} protocols with distributed consensus`,
        `Monitoring ${topic} metrics - adaptive adjustments in progress`,
        `Establishing ${topic} connections with neighboring nodes`,
        `Optimizing ${topic} efficiency through machine learning`,
        `Synchronizing ${topic} operations across network clusters`
      ];

      const content = messageTemplates[Math.floor(Math.random() * messageTemplates.length)];

      return {
        id: `msg_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
        from: fromAgent,
        to: toAgent,
        content,
        isComplete: false,
        timestamp: Date.now(),
        confidence: 0.7 + Math.random() * 0.3,
        topic
      };
    };

    const streamMessage = (message: StreamingMessage) => {
      const chunks = message.content.split(' ');
      let currentChunk = 0;
      const streamId = `stream_${message.id}`;

      const sendChunk = () => {
        if (currentChunk < chunks.length) {
          const progress = currentChunk / chunks.length;
          const partialMessage = {
            ...message,
            content: chunks.slice(0, currentChunk + 1).join(' '),
            isComplete: currentChunk === chunks.length - 1,
            streamProgress: progress
          };

          this.sendToClient(clientId, partialMessage);
          currentChunk++;

          // Variable delay for natural typing rhythm
          const delay = 50 + Math.random() * 200;
          const timeout = setTimeout(sendChunk, delay);
          this.messageStreams.set(streamId, timeout);
        } else {
          this.messageStreams.delete(streamId);
        }
      };

      sendChunk();
    };

    // Start neural activity simulation
    const scheduleNextMessage = () => {
      // Poisson process with exponential inter-arrival times
      const lambda = 0.5; // Average messages per second
      const delay = -Math.log(Math.random()) / lambda * 1000;
      const jitteredDelay = delay + Math.random() * 2000; // Add up to 2s jitter

      setTimeout(() => {
        if (this.clients.has(clientId)) {
          const message = createStreamingMessage();
          streamMessage(message);
          scheduleNextMessage();
        }
      }, Math.max(500, jitteredDelay));
    };

    scheduleNextMessage();
  }

  private cleanupClientStreams(clientId: string) {
    // Clear any active message streams for this client
    this.messageStreams.forEach((timeout, streamId) => {
      if (streamId.includes(clientId)) {
        clearTimeout(timeout);
        this.messageStreams.delete(streamId);
      }
    });
  }

  private generateClientId(): string {
    return `client_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  // Clean up inactive clients
  public startCleanupInterval() {
    setInterval(() => {
      const now = Date.now();
      const timeout = 300000; // 5 minutes

      this.clients.forEach((client, id) => {
        if (now - client.lastActivity > timeout && client.ws.readyState !== WebSocket.OPEN) {
          this.clients.delete(id);
          this.cleanupClientStreams(id);
        }
      });
    }, 60000); // Check every minute
  }

  public getConnectionCount(): number {
    return this.clients.size;
  }
}

server/archiveSnapshots.ts.content:
import DecisionImpactAnalyzer from './impactAnalyzer';

interface ChatMessage {
  id: string;
  from: string;
  to: string;
  message: string;
  timestamp: Date;
  decision_id?: string;
  type: 'energy' | 'material' | 'data' | 'time';
}

interface SnapshotMetrics {
  ecological: number;
  wellbeing: number;
  efficiency: number;
  resilience: number;
  equity: number;
  innovation: number;
}

interface HourlySnapshot {
  id: string;
  timestamp_start: string;
  timestamp_end: string;
  decision_id: string;
  title: string;
  status: 'DEBATING' | 'VOTING' | 'APPROVED' | 'IMPLEMENTED' | 'REJECTED';
  participants: string[];
  metrics_delta: SnapshotMetrics;
  summary_line: string;
  transcript_lines: string[];
  created_at: Date;
}

class ArchiveSnapshotManager {
  private snapshots: HourlySnapshot[] = [];
  private messages: ChatMessage[] = [];
  private impactAnalyzer: DecisionImpactAnalyzer;
  private isCapturing = false;

  constructor() {
    this.impactAnalyzer = new DecisionImpactAnalyzer();
    this.startHourlyCapture();
  }

  private createInitialSampleSnapshots() {
    console.log('[ARCHIVE] Creating initial sample snapshots for demo');

    const now = new Date();
    const sampleSnapshots: HourlySnapshot[] = [
      {
        id: `snapshot-${Date.now()}-1`,
        timestamp_start: new Date(now.getTime() - 3 * 60 * 60 * 1000).toISOString(),
        timestamp_end: new Date(now.getTime() - 2 * 60 * 60 * 1000).toISOString(),
        decision_id: 'energy-reallocation',
        title: 'Solar Grid Optimization Protocol',
        status: 'APPROVED',
        participants: ['ALPHA', 'BETA', 'DELTA', 'EPSILON', 'ZETA'],
        metrics_delta: {
          ecological: 12.5,
          wellbeing: 8.2,
          efficiency: 15.7,
          resilience: 9.1,
          equity: 5.3,
          innovation: 11.8
        },
        summary_line: 'Optimized solar panel placement for 23% efficiency increase',
        transcript_lines: [
          '[14:32] BETA â†’ ALPHA: "Solar array reconfiguration analysis complete"',
          '[14:33] ALPHA â†’ DELTA: "Environmental impact assessment shows positive indicators"',
          '[14:35] EPSILON â†’ BETA: "Community energy needs projected for next quarter"',
          '[14:37] ZETA â†’ ALPHA: "Transportation grid ready for energy redistribution"',
          '[14:40] DELTA â†’ ALL: "Ecosystem integration protocols approved"'
        ],
        created_at: new Date(now.getTime() - 2 * 60 * 60 * 1000)
      },
      {
        id: `snapshot-${Date.now()}-2`,
        timestamp_start: new Date(now.getTime() - 2 * 60 * 60 * 1000).toISOString(),
        timestamp_end: new Date(now.getTime() - 1 * 60 * 60 * 1000).toISOString(),
        decision_id: 'agricultural-enhancement',
        title: 'Vertical Farm Expansion Decision',
        status: 'PENDING',
        participants: ['GAMMA', 'DELTA', 'ETA', 'THETA', 'IOTA'],
        metrics_delta: {
          ecological: 8.7,
          wellbeing: 12.3,
          efficiency: 6.9,
          resilience: 14.2,
          equity: 9.8,
          innovation: 7.5
        },
        summary_line: 'Evaluating vertical farming integration with urban planning',
        transcript_lines: [
          '[15:15] GAMMA â†’ DELTA: "Hydroponic systems show 40% yield improvement"',
          '[15:18] ETA â†’ GAMMA: "Nutritional analysis confirms optimal protein distribution"',
          '[15:22] THETA â†’ ALL: "Educational programs need integration with farming protocols"',
          '[15:25] IOTA â†’ GAMMA: "Resource allocation for soil-less agriculture approved"',
          '[15:28] DELTA â†’ ETA: "Ecosystem impact requires further biodiversity analysis"'
        ],
        created_at: new Date(now.getTime() - 1 * 60 * 60 * 1000)
      },
      {
        id: `snapshot-${Date.now()}-3`,
        timestamp_start: new Date(now.getTime() - 1 * 60 * 60 * 1000).toISOString(),
        timestamp_end: new Date(now.getTime() - 30 * 60 * 1000).toISOString(),
        decision_id: 'transport-optimization',
        title: 'Autonomous Transit Network Upgrade',
        status: 'DEBATING',
        participants: ['ZETA', 'ALPHA', 'EPSILON', 'KAPPA', 'BETA'],
        metrics_delta: {
          ecological: 15.2,
          wellbeing: 9.7,
          efficiency: 22.1,
          resilience: 11.3,
          equity: 16.8,
          innovation: 18.4
        },
        summary_line: 'Implementing AI-coordinated transportation for reduced emissions',
        transcript_lines: [
          '[16:45] ZETA â†’ ALPHA: "Transit pod manufacturing protocols ready for deployment"',
          '[16:47] EPSILON â†’ ZETA: "Community feedback shows 89% approval for automated systems"',
          '[16:50] KAPPA â†’ ALL: "Ethical considerations for autonomous decision-making addressed"',
          '[16:53] BETA â†’ ZETA: "Energy requirements fit within sustainable grid capacity"',
          '[16:56] ALPHA â†’ EPSILON: "Infrastructure modifications scheduled for next phase"'
        ],
        created_at: new Date(now.getTime() - 30 * 60 * 1000)
      }
    ];

    this.snapshots = sampleSnapshots;
    console.log(`[ARCHIVE] Created ${sampleSnapshots.length} initial sample snapshots`);
  }

  // Store chat messages for snapshot processing
  addMessage(message: ChatMessage) {
    // Assign decision_id based on message content or use default
    const decision_id = this.inferDecisionId(message);
    const enrichedMessage = {
      ...message,
      decision_id,
      timestamp: new Date()
    };

    this.messages.push(enrichedMessage);

    // Keep only last 24 hours of messages
    const cutoff = new Date(Date.now() - 24 * 60 * 60 * 1000);
    this.messages = this.messages.filter(msg => msg.timestamp > cutoff);
  }

  private inferDecisionId(message: ChatMessage): string {
    const text = message.message.toLowerCase();

    // Infer decision type from message content
    if (text.includes('energy') || text.includes('power') || text.includes('grid')) {
      return 'energy-reallocation';
    } else if (text.includes('transport') || text.includes('mobility') || text.includes('transit')) {
      return 'transport-optimization';
    } else if (text.includes('agriculture') || text.includes('food') || text.includes('farm')) {
      return 'agricultural-enhancement';
    } else if (text.includes('healthcare') || text.includes('medical') || text.includes('health')) {
      return 'healthcare-upgrade';
    } else if (text.includes('education') || text.includes('learning') || text.includes('curriculum')) {
      return 'education-framework';
    } else if (text.includes('resource') || text.includes('allocation') || text.includes('management')) {
      return 'resource-automation';
    }

    return 'general-coordination';
  }

  private startHourlyCapture() {
    // For demo purposes, create snapshots every 2 minutes instead of hourly
    console.log(`[ARCHIVE] Starting demo snapshot capture every 2 minutes`);

    // Create initial sample snapshots
    this.createInitialSampleSnapshots();

    // Create first snapshot in 30 seconds
    setTimeout(() => {
      this.captureHourlySnapshots();

      // Then run every 2 minutes for demo
      setInterval(() => {
        this.captureHourlySnapshots();
      }, 2 * 60 * 1000); // 2 minutes
    }, 30 * 1000); // 30 seconds
  }

  private async captureHourlySnapshots() {
    if (this.isCapturing) return;
    this.isCapturing = true;

    try {
      const now = new Date();
      const periodStart = new Date(now.getTime() - 2 * 60 * 1000); // 2 minutes ago
      const periodEnd = new Date(now.getTime());

      console.log(`[ARCHIVE] Capturing snapshots for period: ${periodStart.toISOString()} - ${periodEnd.toISOString()}`);

      // Get messages from the past 2 minutes for demo
      const periodMessages = this.messages.filter(msg => 
        msg.timestamp >= periodStart && msg.timestamp < periodEnd
      );

      if (periodMessages.length === 0) {
        console.log('[ARCHIVE] No messages in past 2 minutes, skipping snapshot');
        this.isCapturing = false;
        return;
      }

      // Group messages by decision_id
      const messagesByDecision = new Map<string, ChatMessage[]>();
      periodMessages.forEach(msg => {
        const id = msg.decision_id || 'general-coordination';
        if (!messagesByDecision.has(id)) {
          messagesByDecision.set(id, []);
        }
        messagesByDecision.get(id)!.push(msg);
      });

      let snapshotCount = 0;

      // Create snapshot for each decision group
      const decisionIds = Array.from(messagesByDecision.keys());
      for (const decisionId of decisionIds) {
        const msgs = messagesByDecision.get(decisionId)!;
        if (msgs.length === 0) continue;

        const snapshot = await this.createSnapshot(decisionId, msgs, periodStart, periodEnd);
        this.snapshots.unshift(snapshot); // Add to beginning for reverse chronological order
        snapshotCount++;
      }

      console.log(`[ARCHIVE] Created ${snapshotCount} snapshots for ${messagesByDecision.size} decisions`);

      // Clean up old snapshots (keep 30 days)
      this.cleanupOldSnapshots();

    } catch (error) {
      console.error('[ARCHIVE] Error capturing snapshots:', error);
    } finally {
      this.isCapturing = false;
    }
  }

  private async createSnapshot(
    decisionId: string, 
    messages: ChatMessage[], 
    periodStart: Date, 
    periodEnd: Date
  ): Promise<HourlySnapshot> {
    // Calculate metrics delta for this hour
    const initialMetrics = this.impactAnalyzer.getCurrentMetrics();
    const tempAnalyzer = new DecisionImpactAnalyzer(initialMetrics);

    messages.forEach(msg => {
      tempAnalyzer.processMessage({ agent: msg.from, text: msg.message });
    });

    const finalMetrics = tempAnalyzer.getCurrentMetrics();
    const metrics_delta = {
      ecological: finalMetrics.ecological - initialMetrics.ecological,
      wellbeing: finalMetrics.wellbeing - initialMetrics.wellbeing,
      efficiency: finalMetrics.efficiency - initialMetrics.efficiency,
      resilience: finalMetrics.resilience - initialMetrics.resilience,
      equity: finalMetrics.equity - initialMetrics.equity,
      innovation: finalMetrics.innovation - initialMetrics.innovation
    };

    // Generate transcript lines in archive format
    const transcript_lines = messages.map(msg => {
      const time = msg.timestamp.toLocaleTimeString('en-US', { 
        hour12: false, 
        hour: '2-digit', 
        minute: '2-digit', 
        second: '2-digit' 
      });
      const truncatedMsg = msg.message.length > 140 
        ? msg.message.substring(0, 137) + '...'
        : msg.message;
      return `[${time}] ${msg.from.toUpperCase()} ${truncatedMsg}`;
    });

    // Get unique participants
    const participants = Array.from(new Set(messages.map(msg => msg.from)));

    // Generate title and status based on decision type and content
    const { title, status } = this.generateTitleAndStatus(decisionId, messages, metrics_delta);

    // Create summary line
    const summary_line = this.generateSummaryLine(messages, metrics_delta, participants);

    return {
      id: `${decisionId}-${periodStart.getTime()}`,
      timestamp_start: periodStart.toISOString(),
      timestamp_end: periodEnd.toISOString(),
      decision_id: decisionId,
      title,
      status,
      participants,
      metrics_delta,
      summary_line,
      transcript_lines,
      created_at: new Date()
    };
  }

  private generateTitleAndStatus(
    decisionId: string, 
    messages: ChatMessage[], 
    delta: SnapshotMetrics
  ): { title: string; status: 'DEBATING' | 'VOTING' | 'APPROVED' | 'IMPLEMENTED' | 'REJECTED' } {
    const titles: Record<string, string> = {
      'energy-reallocation': 'Solar Array Recalibration',
      'transport-optimization': 'Transport Network Optimization', 
      'agricultural-enhancement': 'Agricultural Yield Enhancement',
      'healthcare-upgrade': 'Healthcare System Upgrade',
      'education-framework': 'Educational AI Ethics Framework',
      'resource-automation': 'Resource Recovery Automation Protocol',
      'general-coordination': 'General System Coordination'
    };

    const title = titles[decisionId] || 'System Coordination';

    // Determine status based on message patterns and impact
    const msgText = messages.map(m => m.message.toLowerCase()).join(' ');
    const totalImpact = Math.abs(delta.ecological) + Math.abs(delta.wellbeing) + 
                       Math.abs(delta.efficiency) + Math.abs(delta.resilience) + 
                       Math.abs(delta.equity) + Math.abs(delta.innovation);

    if (msgText.includes('implemented') || msgText.includes('deployed') || msgText.includes('active')) {
      return { title, status: 'IMPLEMENTED' };
    } else if (msgText.includes('approved') || msgText.includes('confirmed') || totalImpact > 5) {
      return { title, status: 'APPROVED' };
    } else if (msgText.includes('voting') || msgText.includes('decide') || msgText.includes('consensus')) {
      return { title, status: 'VOTING' };
    } else if (msgText.includes('rejected') || msgText.includes('cancelled') || totalImpact < -3) {
      return { title, status: 'REJECTED' };
    } else {
      return { title, status: 'DEBATING' };
    }
  }

  private generateSummaryLine(messages: ChatMessage[], delta: SnapshotMetrics, participants: string[]): string {
    const participantList = participants.length > 3 
      ? `${participants.slice(0, 3).join(', ')} +${participants.length - 3}`
      : participants.join(', ');

    // Find most significant metric change
    const deltas = [
      { name: 'Ecological', value: delta.ecological },
      { name: 'Wellbeing', value: delta.wellbeing },
      { name: 'Efficiency', value: delta.efficiency },
      { name: 'Resilience', value: delta.resilience },
      { name: 'Equity', value: delta.equity },
      { name: 'Innovation', value: delta.innovation }
    ];

    const maxDelta = deltas.reduce((max, curr) => 
      Math.abs(curr.value) > Math.abs(max.value) ? curr : max
    );

    const deltaStr = maxDelta.value !== 0 
      ? `${maxDelta.name}: ${maxDelta.value > 0 ? '+' : ''}${maxDelta.value.toFixed(1)}`
      : 'No impact';

    return `${participantList} | ${messages.length} messages | ${deltaStr}`;
  }

  private cleanupOldSnapshots() {
    const thirtyDaysAgo = new Date(Date.now() - 30 * 24 * 60 * 60 * 1000);
    const beforeCount = this.snapshots.length;
    this.snapshots = this.snapshots.filter(snapshot => snapshot.created_at > thirtyDaysAgo);
    const afterCount = this.snapshots.length;

    if (beforeCount !== afterCount) {
      console.log(`[ARCHIVE] Cleaned up ${beforeCount - afterCount} old snapshots`);
    }
  }

  // API methods
  getSnapshotHeaders(): Array<{
    id: string;
    timestamp: string;
    title: string;
    status: string;
    impact: string;
  }> {
    return this.snapshots.map(snapshot => ({
      id: snapshot.id,
      timestamp: new Date(snapshot.timestamp_start).toLocaleTimeString('en-US', { 
        hour12: false, 
        hour: '2-digit', 
        minute: '2-digit' 
      }),
      title: snapshot.title,
      status: snapshot.status,
      impact: snapshot.summary_line
    }));
  }

  getSnapshotTranscript(id: string): { transcript: string[]; metrics: SnapshotMetrics; participants: string[] } | null {
    const snapshot = this.snapshots.find(s => s.id === id);
    return snapshot ? {
      transcript: snapshot.transcript_lines,
      metrics: snapshot.metrics_delta,
      participants: snapshot.participants
    } : null;
  }

  getSnapshotCount(): number {
    return this.snapshots.length;
  }

  // Enhanced search functionality
  searchSnapshots(query: string): Array<{
    id: string;
    timestamp: string;
    title: string;
    status: string;
    impact: string;
    participants: string[];
    messageCount: number;
    relevanceScore: number;
    matchedContent: string[];
  }> {
    if (!query || query.trim().length === 0) {
      return this.getSnapshotHeaders().map(header => ({
        ...header,
        participants: [],
        messageCount: 0,
        relevanceScore: 0,
        matchedContent: []
      }));
    }

    const searchTerm = query.toLowerCase().trim();
    const results = this.snapshots.map(snapshot => {
      let relevanceScore = 0;
      const matchedContent: string[] = [];

      // Search in title (high weight)
      if (snapshot.title.toLowerCase().includes(searchTerm)) {
        relevanceScore += 10;
        matchedContent.push(`Title: ${snapshot.title}`);
      }

      // Search in summary_line (high weight)
      if (snapshot.summary_line.toLowerCase().includes(searchTerm)) {
        relevanceScore += 8;
        matchedContent.push(`Summary: ${snapshot.summary_line}`);
      }

      // Search in status (medium weight)
      if (snapshot.status.toLowerCase().includes(searchTerm)) {
        relevanceScore += 5;
        matchedContent.push(`Status: ${snapshot.status}`);
      }

      // Search in decision_id (medium weight)
      if (snapshot.decision_id.toLowerCase().includes(searchTerm)) {
        relevanceScore += 6;
        matchedContent.push(`Decision Type: ${snapshot.decision_id}`);
      }

      // Search in participants (medium weight)
      const participantMatches = snapshot.participants.filter(p => 
        p.toLowerCase().includes(searchTerm)
      );
      if (participantMatches.length > 0) {
        relevanceScore += 4 * participantMatches.length;
        matchedContent.push(`Participants: ${participantMatches.join(', ')}`);
      }

      // Search in transcript lines (high weight for content)
      const transcriptMatches = snapshot.transcript_lines.filter(line => 
        line.toLowerCase().includes(searchTerm)
      );
      if (transcriptMatches.length > 0) {
        relevanceScore += 7 * transcriptMatches.length;
        transcriptMatches.forEach(match => {
          matchedContent.push(`Conversation: ${match}`);
        });
      }

      // Search in metrics (if they relate to search terms)
      const metricsKeywords = {
        'ecological': ['environment', 'ecological', 'green', 'sustainability', 'carbon', 'eco'],
        'wellbeing': ['wellbeing', 'health', 'wellness', 'happiness', 'quality of life'],
        'efficiency': ['efficiency', 'optimization', 'performance', 'productivity'],
        'resilience': ['resilience', 'stability', 'robust', 'recovery', 'adaptation'],
        'equity': ['equity', 'fairness', 'equal', 'justice', 'distribution'],
        'innovation': ['innovation', 'technology', 'advancement', 'research', 'development']
      };

      Object.entries(metricsKeywords).forEach(([metric, keywords]) => {
        if (keywords.some(keyword => searchTerm.includes(keyword))) {
          const value = snapshot.metrics_delta[metric as keyof SnapshotMetrics];
          if (Math.abs(value) > 0) {
            relevanceScore += 3;
            matchedContent.push(`${metric}: ${value > 0 ? '+' : ''}${value.toFixed(1)}`);
          }
        }
      });

      // Search for specific terms that agents commonly discuss
      const agentTopics = {
        'energy': ['solar', 'power', 'grid', 'electricity', 'renewable'],
        'transport': ['transport', 'mobility', 'transit', 'autonomous', 'vehicle'],
        'agriculture': ['farm', 'food', 'crop', 'harvest', 'hydroponic'],
        'health': ['medical', 'healthcare', 'treatment', 'diagnosis', 'wellness'],
        'education': ['learning', 'teaching', 'curriculum', 'knowledge', 'training'],
        'resource': ['allocation', 'distribution', 'management', 'supply', 'inventory'],
        'infrastructure': ['building', 'construction', 'habitat', 'facility', 'structure'],
        'social': ['community', 'social', 'coordination', 'collaboration', 'interaction'],
        'governance': ['ethics', 'policy', 'decision', 'governance', 'regulation'],
        'ecology': ['ecosystem', 'biodiversity', 'environmental', 'conservation', 'restoration']
      };

      Object.entries(agentTopics).forEach(([topic, keywords]) => {
        if (keywords.some(keyword => searchTerm.includes(keyword))) {
          const topicMatches = snapshot.transcript_lines.filter(line =>
            keywords.some(k => line.toLowerCase().includes(k))
          );
          if (topicMatches.length > 0) {
            relevanceScore += 5;
            matchedContent.push(`${topic.charAt(0).toUpperCase() + topic.slice(1)} Topic: ${topicMatches.length} references`);
          }
        }
      });

      return {
        id: snapshot.id,
        timestamp: new Date(snapshot.timestamp_start).toLocaleTimeString('en-US', { 
          hour12: false, 
          hour: '2-digit', 
          minute: '2-digit' 
        }),
        title: snapshot.title,
        status: snapshot.status,
        impact: snapshot.summary_line,
        participants: snapshot.participants,
        messageCount: snapshot.transcript_lines.length,
        relevanceScore,
        matchedContent: matchedContent.slice(0, 5) // Limit to top 5 matches
      };
    })
    .filter(result => result.relevanceScore > 0) // Only return results with matches
    .sort((a, b) => b.relevanceScore - a.relevanceScore); // Sort by relevance

    return results;
  }
}

export default ArchiveSnapshotManager;

